external min
  :  (float[@unboxed])
  -> (float[@unboxed])
  -> (float[@unboxed])
  @@ portable
  = "caml_sse2_float64_min_bytecode" "caml_sse2_float64_min"
[@@noalloc]

external max
  :  (float[@unboxed])
  -> (float[@unboxed])
  -> (float[@unboxed])
  @@ portable
  = "caml_sse2_float64_max_bytecode" "caml_sse2_float64_max"
[@@noalloc]

external iround_current
  :  (float[@unboxed])
  -> (int64[@unboxed])
  @@ portable
  = "caml_sse2_cast_float64_int64_bytecode" "caml_sse2_cast_float64_int64"
[@@noalloc]

module Unboxed = struct
  external min
    :  (float#[@unboxed])
    -> (float#[@unboxed])
    -> (float#[@unboxed])
    @@ portable
    = "caml_sse2_float64_min_bytecode" "caml_sse2_float64_min"
  [@@noalloc]

  external max
    :  (float#[@unboxed])
    -> (float#[@unboxed])
    -> (float#[@unboxed])
    @@ portable
    = "caml_sse2_float64_max_bytecode" "caml_sse2_float64_max"
  [@@noalloc]

  external iround_current
    :  (float#[@unboxed])
    -> (int64#[@unboxed])
    @@ portable
    = "caml_sse2_cast_float64_int64_bytecode" "caml_sse2_cast_float64_int64"
  [@@noalloc]
end
